'use strict';
import errors from '@hoist/errors';
import Pipeline from '@hoist/connector-pipeline';
import BaseAPI from './base_api';
import {
  filter, functions
}
from 'lodash';
import logger from '@hoist/logger';
import Bluebird from 'bluebird';
/**
 * Hoists API wrapper around connectors
 */
class ConnectorAPI extends BaseAPI {
  /**
   * create a new connector API
   * @param {string} key - the connector key
   */
  constructor(key) {
    super();
    this._logger = logger.child({
      cls: this.constructor.name
    });
    if (!key) {
      throw new errors.connector.request.InvalidError();
    }
    this._pipeline = new Pipeline();
    this._key = key;
    this._loadConnector();
  }
  _loadConnector() {
    if (!this._connector) {
      this._connector = this._pipeline.loadConnector(this._key).then((c) => {

        let methods = filter(functions(c), (property) => {

          if (property.startsWith('_') || property === 'receiveBounce' || this[property]) {
            return false;
          } else {
            return true;
          }
        });
        //we should always show the rest params but they might return an error
        methods = methods.concat(['get', 'post', 'put', 'delete']);
        methods.forEach((method) => {
          /**
           * also has all methods of underlying connector
           */
          let _this = this;
          this[method] = function () {
            _this._logger.info('proxying method ' + method);
            if (typeof c[method] !== 'function') {
              throw new errors.connector.request.UnsupportedError(method + ' method unsupported');
            }
            let callback;
            let args = Array.prototype.slice.call(arguments);
            if (typeof (args[args.length - 1]) === 'function') {
              callback = args.pop();
            }
            return Bluebird.resolve(c[method].apply(c, args))
              .nodeify(callback);
          };
        });
      });

    }
    return this._connector;
  }
  init() {
    return this._loadConnector();
  }
  get(...params) {
    return this.loadConnector()
      .then(() => {
        return this.get.apply(this, params);
      });
  }
  put(...params) {
    return this.loadConnector()
      .then(() => {
        return this.put.apply(this, params);
      });

  }
  post(...params) {
    return this.loadConnector()
      .then(() => {
        return this.post.apply(this, params);
      });
  }
  delete(...params) {
    return this.loadConnector()
      .then(() => {
        return this.delete.apply(this, params);
      });
  }
}

export default ConnectorAPI;

'use strict';
import Pipeline from '@hoist/data-pipeline';
import errors from '@hoist/errors';
import Bluebird from 'bluebird';
import BaseAPI from './base_api';

/**
 * Hoists Data API
 */
class DataAPI extends BaseAPI {
  /**
   * create a new data API
   * @param {string} [type] - the object type for this data api instance
   */
  constructor(type) {
    super();
    if (type) {
      this.setType(type);
    }
    this._pipeline = new Pipeline();
  }

  _verify() {
    return Promise.resolve().then(() => {
      if (!this._type) {
        throw new errors.data.request.InvalidError('you need to specify a type for the retrieval, call #setType([typename]) first');
      }
    });
  }

  /**
   * set the type of this Data API instance
   * @param {string} type - the object type for this data api instance
   */
  setType(type) {
    this._type = type.toLowerCase();
  }

  /**
   * find objects matching the given query
   * @param {object} query - the mongo style query
   * @param {function(error: Error, objects: Array<object>)} [callback] - callback to call when objects have been found
   * @returns {Promise<Array<object>>} - a promise to have retrieved the objects
   */
  find(query, callback) {
    return Bluebird.resolve(
        this._verify()
        .then(() => {
          return this._pipeline.find(this._type, query || {});
        }))
      .nodeify(callback);
  }

  /**
   * find a single object with the matching id
   * @param {string} id - the object id
   * @param {function(error: Error, object: object)} [callback] - callback to call when object has been found
   * @returns {Promise<object>} - a promise to have retrieved the object
   */
  findById(id, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!id) {
            throw new errors.data.request.InvalidError('you need to specify an id for #findById');
          }
        })
        .then(() => {
          return this.findOne({
            _id: id
          });
        }))
      .nodeify(callback);
  }

  /**
   * find a single object matching the given query
   * @param {object} query - the mongo style query
   * @param {function(error: Error, object: object)} [callback] - callback to call when object has been found
   * @returns {Promise<object>} - a promise to have retrieved the object
   */
  findOne(query, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!query) {
            throw new errors.data.request.InvalidError('you need to specify a query for #findOne');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.findOne(this._type, query);
        }))
      .nodeify(callback);
  }

  /**
   * remove objects matching the given query
   * @param {object} query - the mongo style query
   * @param {function(error: Error)} [callback] - callback to call when objects have been removed
   * @returns {Promise} - a promise to have removed the objects
   */
  remove(query, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!query) {
            throw new errors.data.request.InvalidError('you need to specify a query for #remove');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.remove(this._type, query);
        }))
      .nodeify(callback);
  }

  /**
   * remove a single object with a given id
   * @param {string} id - the object id
   * @param {function(error: Error)} [callback] - callback to call when object has been removed
   * @returns {Promise} - a promise to have removed the object
   */
  removeOneById(id, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!id) {
            throw new errors.data.request.InvalidError('you need to specify an id for #removeOneById');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.remove(this._type, {
            _id: id
          });
        }))
      .nodeify(callback);
  }

  /**
   * save an object
   * @param {object} json - raw object to save
   * @param {function(error: Error, object: object)} [callback] - callback to call when objects has been saved
   * @returns {Promise<object>} - a promise to have saved the object
   */
  save(json, callback) {
    if (!this._type && json._type) {
      this.setType(json._type);
    }
    return Bluebird.resolve(
      this._verify()
      .then(() => {
        return this._pipeline.save(this._type, json);
      })).nodeify(callback);
  }
}


export default DataAPI;

'use strict';
import Pipeline from '@hoist/events-pipeline';
import {
  isFunction
}
from 'lodash';
import Bluebird from 'bluebird';
import BaseAPI from './base_api';
import Context from '@hoist/context';

/**
 * Hoists event API
 */
class EventsAPI extends BaseAPI {
  /**
   * create a new instance of the event api
   */
  constructor() {
    super();
    this._pipeline = new Pipeline();
  }

  /**
   * raise a new event
   * @param {string} name - the name of the event
   * @param {object} payload - the event payload
   * @param {object} [contextOverride] - overides for the current context
   * @param {function(error: Error, objects: Event)} [callback] - callback to call when event has been raised
   * @returns {Promise<Event>} - a promise to have raised the Event
   */
  raise(name, payload, contextOverride, callback) {
    if (!callback && isFunction(contextOverride)) {
      callback = contextOverride;
      contextOverride = null;
    }
    if (!callback && isFunction(payload)) {
      callback = payload;
      payload = {};
    }
    return Bluebird.resolve(
      Context.get().then((context) => {
        return this._pipeline.raise(context, name, payload, contextOverride);
      })
    ).nodeify(callback);
  }
}

export default EventsAPI;

'use strict';
import {
  isFunction
}
from 'lodash';
import LockAPI from './lock';
import Bluebird from 'bluebird';
import DataAPI from './data';
import UserAPI from './user';
import EventsAPI from './events';
import ConnectorAPI from './connector';
import log from './log';
import TimeoutAPI from './timeout';
import Context from '@hoist/context';
import Model from '@hoist/model';
import BucketAPI from './bucket';

let index = {
  data: function (type) {
    return new DataAPI(type);
  },
  user: new UserAPI(),
  events: new EventsAPI(),
  connector: function (key) {
    return new ConnectorAPI(key);
  },
  log: log,
  lock: function CreateLock(name, timeout, callback) {
    let hoistLock = new LockAPI();
    if (!callback && isFunction(timeout)) {
      callback = timeout;
      timeout = null;
    }
    return Bluebird.resolve(hoistLock.aquireLock(name, timeout))
      .nodeify(callback);
  },
  timeout: new TimeoutAPI(),
  Context: Context,
  _model: Model,
  bucket: new BucketAPI()
};

index.event = index.events;

export default index;

'use strict';
import config from 'config';
import redisLock from 'redis-lock';
import redis from 'redis';
import BaseAPI from './base_api';

let client;
/**
 * Hoists lock API
 */
class HoistLockAPI extends BaseAPI {

  /**
   * create new lock
   */
  constructor() {
    super();
  }

  /**
   * aquire a lock within the given timeout
   * @param {string} name - the key for the lock
   * @param {Number} [timeout=500] - the timeout to wait for the lock
   * @returns {Promise} - promise to have aquired the lock
   */
  aquireLock(name, timeout) {
    timeout = timeout || 500;
    client = HoistLockAPI._getClient();
    let lock = redisLock(client);
    return new Promise((resolve) => {
      lock(name, timeout, (done) => {
        /*
         * release the current lock
         */
        resolve({
          release: done
        });
      });
    });
  }
}


HoistLockAPI._getClient = function () {
  return client || (client = redis.createClient(config.get('Hoist.redis.port'), config.get('Hoist.redis.host')));
};
HoistLockAPI._clearClient = function () {
  if (client) {
    client.end();
    client = null;
  }
};



export default HoistLockAPI;

'use strict';
import Bluebird from 'bluebird';
import {
  isFunction
}
from 'lodash';

/**
 * log out the object to be captured by the executor process
 */
export default function Log() {

  let args = Array.prototype.slice.call(arguments);
  let callback;
  if (isFunction(args[args.length - 1])) {
    callback = args.pop();
  }
  let log = {
    type: 'appLog',
    arguments: args
  };

  let cache = [];
  let message = JSON.stringify(log, function (key, value) {
    if (typeof value === 'object' && value !== null) {
      if (cache.indexOf(value) !== -1) {
        return '[Circular]';
      }
      // Store value in our collection
      cache.push(value);
    }
    return value;
  });
  cache = null;
  console.log(message);

  //return promise for backwards compatibility

  return Bluebird.resolve(null).nodeify(callback);
}

'use strict';
import Context from '@hoist/context';
import Errors from '@hoist/errors';
import {
  isNumber
}
from 'lodash';
import BaseAPI from './base_api';

/**
 * the Hoist Timeout API
 */
class TimeoutAPI extends BaseAPI {
  /**
   * reset the timeout from now
   * @param {Number} milliseconds - the amount of time to timeout from now (between 1 and 120000)
   * @returns {Promise}
   */
  reset(milliseconds) {
    //upper bound at 2m
    //lower bound at 1s
    return Promise.resolve().then(() => {

      if (!milliseconds || !isNumber(milliseconds) || milliseconds > 120000 || milliseconds < 1) {
        throw new Errors.timeout.InvalidTimeoutValueError();
      }

      return Context.get().then((context) => {
        if (context.timeout) {
          clearTimeout(context.timeout.current);
          context.timeout.current = setTimeout(context.timeout.onTimeout, milliseconds);
        }
      });
    });

  }
}


export default TimeoutAPI;

'use strict';
import Errors from '@hoist/errors';
import Pipeline from '@hoist/user-pipeline';
import BaseAPI from './base_api';
import Bluebird from 'bluebird';

/**
 * Hoists User API
 * @depreciated
 */
class UserAPI extends BaseAPI {
  /**
   * create a user API
   */
  constructor() {
    super();
    this._pipeline = new Pipeline();
  }

  /**
   * log the user in
   * @params {string} username - the username
   * @params {password} password - the password
   */
  login(username, password, callback) {
    if (!username) {
      throw new Errors.user.request.InvalidError('username is required');
    }
    if (!password) {
      throw new Errors.user.request.InvalidError('password is required');
    }
    return Bluebird.resolve(this._pipeline.login(username, password))
      .nodeify(callback);
  }
  invite(userDetails, callback) {
    if (!userDetails) {
      throw new Errors.user.request.InvalidError('user details are required');
    }
    return Bluebird.resolve(this._pipeline.invite(userDetails))
      .nodeify(callback);
  }
}

export default UserAPI;

'use strict';
import Hoist from '../../src';
import {
  expect
}
from 'chai';
import sinon from 'sinon';
import BucketPipeline from '@hoist/bucket-pipeline';
import Errors from '@hoist/errors';

/** @test {BucketAPI} */
describe('Hoist.bucket', function () {
  it('exists', function () {
    return expect(Hoist.bucket).to.exist;
  });
  /** @test {BucketAPI#remove} */
  describe('.remove', function () {
    before(function () {
      sinon.stub(BucketPipeline.prototype, 'remove').returns(Promise.resolve(null));
      return Hoist.bucket.remove('key');
    });
    after(function () {
      BucketPipeline.prototype.remove.restore();
    });
    it('calls bucketPipeline.remove', function () {
      return expect(BucketPipeline.prototype.remove)
        .to.have.been.calledWith('key');
    });
  });
  /** @test {BucketAPI#set} */
  describe('.set', function () {
    describe('with key and create', function () {
      var fakeKey = 'key';
      before(function () {
        sinon.stub(BucketPipeline.prototype, 'set').returns(Promise.resolve());
        return Hoist.bucket.set(fakeKey, true);
      });
      after(function () {
        BucketPipeline.prototype.set.restore();
      });
      it('calls bucketPipeline.set with correct key', function () {
        expect(BucketPipeline.prototype.set)
          .to.have.been.calledWith(fakeKey, true);
      });
      it('with callback', function (done) {
        Hoist.bucket.set(fakeKey, true, done);
      });
    });
    describe('with key and callback', function () {
      var fakeKey = 'key';
      before(function (done) {
        sinon.stub(BucketPipeline.prototype, 'set').returns(Promise.resolve());
        return Hoist.bucket.set(fakeKey, done);
      });
      after(function () {
        BucketPipeline.prototype.set.restore();
      });
      it('calls bucketPipeline.set with correct key and meta', function () {
        expect(BucketPipeline.prototype.set)
          .to.have.been.calledWith(fakeKey, null);
      });
    });
    describe('with no key specified', function () {
      it('rejects', function () {
        expect(function () {
          Hoist.bucket.set();
        }).to.throw(Errors.bucket.InvalidError);
      });
      it('with callback', function () {
        expect(function () {
          Hoist.bucket.set(function () {});
        }).to.throw(Errors.bucket.InvalidError);
      });
    });
  });

  /** @test {BucketAPI#add} */
  describe('.add', function () {
    describe('with key and meta', function () {
      var fakeKey = 'key';
      var fakeMeta = 'meta';
      before(function () {
        sinon.stub(BucketPipeline.prototype, 'add').returns(Promise.resolve());
        return Hoist.bucket.add(fakeKey, fakeMeta);
      });
      after(function () {
        BucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(BucketPipeline.prototype.add)
          .to.have.been.calledWith(fakeKey, fakeMeta);
      });
      it('with callback', function (done) {
        Hoist.bucket.add(fakeKey, fakeMeta, done);
      });
    });
    describe('with key and callback', function () {
      var fakeKey = 'key';
      before(function (done) {
        sinon.stub(BucketPipeline.prototype, 'add').returns(Promise.resolve());
        return Hoist.bucket.add(fakeKey, done);
      });
      after(function () {
        BucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(BucketPipeline.prototype.add)
          .to.have.been.calledWith(fakeKey, null);
      });
    });
    describe('with only callback', function () {
      before(function (done) {
        sinon.stub(BucketPipeline.prototype, 'add').returns(Promise.resolve());
        return Hoist.bucket.add(done);
      });
      after(function () {
        BucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(BucketPipeline.prototype.add)
          .to.have.been.calledWith(null, null);
      });
    });
  });

  /** @test {BucketAPI#get} */
  describe('.get', function () {
    describe('with key', function () {
      var fakeKey = 'key';
      before(function () {
        sinon.stub(BucketPipeline.prototype, 'get').returns(Promise.resolve());
        return Hoist.bucket.get(fakeKey);
      });
      after(function () {
        BucketPipeline.prototype.get.restore();
      });
      it('calls bucketPipeline.get with correct key', function () {
        expect(BucketPipeline.prototype.get)
          .to.have.been.calledWith(fakeKey);
      });
      it('with callback', function (done) {
        Hoist.bucket.get(fakeKey, done);
      });
    });
    describe('with only callback', function () {
      before(function (done) {
        sinon.stub(BucketPipeline.prototype, 'get').returns(Promise.resolve());
        Hoist.bucket.get(done);
      });
      after(function () {
        BucketPipeline.prototype.get.restore();
      });
      it('calls bucketPipeline.get with no key', function () {
        expect(BucketPipeline.prototype.get)
          .to.have.been.calledWith();
      });
    });
  });

  /** @test {BucketAPI#getAll} */
  describe('.getAll', function () {
    before(function () {
      sinon.stub(BucketPipeline.prototype, 'getAll').returns(Promise.resolve([]));
      return Hoist.bucket.getAll();
    });
    after(function () {
      BucketPipeline.prototype.getAll.restore();
    });
    it('calls bucketPipeline.get with correct key', function () {
      expect(BucketPipeline.prototype.getAll)
        .to.have.been.calledWith();
    });
    it('with callback', function (done) {
      Hoist.bucket.getAll(done);
    });
  });

  /** @test {BucketAPI#each} */
  describe('.each', function () {
    describe('with a function', function () {
      var fn = function () {};
      before(function () {
        sinon.stub(BucketPipeline.prototype, 'each').returns(Promise.resolve());
        return Hoist.bucket.each(fn);
      });
      after(function () {
        BucketPipeline.prototype.each.restore();
      });
      it('calls bucketPipeline.each with correct key', function () {
        expect(BucketPipeline.prototype.each)
          .to.have.been.calledWith(fn);
      });
      it('with callback', function (done) {
        Hoist.bucket.each(fn, done);
      });
    });
    describe('without function', function () {
      it('rejects', function () {
        expect(function () {
          Hoist.bucket.each();
        }).to.throw(Errors.bucket.InvalidError);
      });
    });
  });
  describe('.saveMeta', function () {
    describe('with meta and key', function () {
      var fakeMeta = {
