'use strict';
import errors from '@hoist/errors';
import Pipeline from '@hoist/connector-pipeline';
import BaseAPI from './base_api';

class ConnectorAPI extends BaseAPI {
  constructor(key) {
    super();
    if (!key) {
      throw new errors.connector.request.InvalidError();
    }
    this._pipeline = new Pipeline();
    this._key = key;
  }
  _loadConnector() {
    return this._connector || (this._connector = this._pipeline.loadConnector(this._key));
  }
  get() {
    var getArgs = arguments;
    return this._loadConnector.then((c) => {
      return c.get.apply(c, getArgs);
    }).catch((err) => {
      this._logger.error(err);
      throw err;
    });
  }
  put() {
    var putArgs = arguments;
    return this._loadConnector.then((c) => {
      return c.put.apply(c, putArgs);
    }).catch((err) => {
      this._logger.error(err);
      throw err;
    });
  }
  post() {
    var postArgs = arguments;
    return this._loadConnector.then((c) => {
      return c.post.apply(c, postArgs);
    }).catch((err) => {
      this._logger.error(err);
      throw err;
    });
  }
  delete() {
    var deleteArgs = arguments;
    return this._loadConnector.then((c) => {
      return c.delete.apply(c, deleteArgs);
    }).catch((err) => {
      this._logger.error(err);
      throw err;
    });
  }
  authorize(token, callback) {
    return this._loadConnector.then((c) => {
      return c.authorize(token);
    }).then(() => {
      return this;
    }).nodeify(callback);
  }
}

export default ConnectorAPI;

'use strict';
import Pipeline from '@hoist/data-pipeline';
import errors from '@hoist/errors';
import Bluebird from 'bluebird';
import BaseAPI from './base_api';

class DataAPI extends BaseAPI {
  constructor(type) {
    super();
    if (type) {
      this.setType(type);
    }
    this._pipeline = new Pipeline();
  }
  _verify() {
    return Promise.resolve().then(() => {
      if (!this._type) {
        throw new errors.data.request.InvalidError('you need to specify a type for the retrieval, call #setType([typename]) first');
      }
    });
  };
  setType(type) {
    this._type = type.toLowerCase();
  }
  find(query, callback) {
    return Bluebird.resolve(
        this._verify()
        .then(() => {
          return this._pipeline.find(this._type, query || {});
        }))
      .nodeify(callback);
  }
  findById(id, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!id) {
            throw new errors.data.request.InvalidError('you need to specify an id for #findById');
          }
        })
        .then(() => {
          return this.findOne({
            _id: id
          });
        }))
      .nodeify(callback);
  }
  findOne(query, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!query) {
            throw new errors.data.request.InvalidError('you need to specify a query for #findOne');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.findOne(this._type, query);
        }))
      .nodeify(callback);
  }
  remove(query, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!query) {
            throw new errors.data.request.InvalidError('you need to specify a query for #remove');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.remove(this._type, query);
        }))
      .nodeify(callback);
  }
  removeOneById(id, callback) {
    return Bluebird.resolve(
        Promise.resolve()
        .then(() => {
          if (!id) {
            throw new errors.data.request.InvalidError('you need to specify an id for #removeOneById');
          }
        })
        .then(() => {
          return this._verify();
        })
        .then(() => {
          return this._pipeline.remove(this._type, {
            _id: id
          });
        }))
      .nodeify(callback);
  }
  save(json, callback) {
    if (!this._type && json._type) {
      this.setType(json._type);
    }
    return Bluebird.resolve(
      this._verify()
      .then(() => {
        return this._pipeline.save(this._type, json);
      })).nodeify(callback);
  }
}


export default DataAPI;

'use strict';
import Pipeline from '@hoist/events-pipeline';
import {
  isFunction
}
from 'lodash';
import Bluebird from 'bluebird';
import BaseAPI from './base_api';

class EventsAPI extends BaseAPI {
  constructor() {
    super();
    this.pipeline = new Pipeline();
  }
  raise(name, payload, contextOverride, callback) {
    if (!callback && isFunction(contextOverride)) {
      callback = contextOverride;
      contextOverride = null;
    }
    if (!callback && isFunction(payload)) {
      callback = payload;
      payload = {};
    }
    return Bluebird.resolve(this.pipeline.raise(name, payload, contextOverride))
      .nodeify(callback);
  }
}

export default EventsAPI;

'use strict';
import {
  isFunction
}
from 'lodash';
import LockAPI from './lock';
import Bluebird from 'bluebird';
import DataAPI from './data';
import UserAPI from './user';
import EventsAPI from './events';
import ConnectorAPI from './connector';
import log from './log';
import TimeoutAPI from './timeout';
import Context from '@hoist/context';
import Model from '@hoist/model';
import BucketAPI from './bucket';

let index = {
  data: function (type) {
    return new DataAPI(type);
  },
  user: new UserAPI(),
  events: new EventsAPI(),
  connector: new ConnectorAPI(),
  log: log,
  lock: function CreateLock(name, timeout, callback) {
    let hoistLock = new LockAPI();
    if (!callback && isFunction(timeout)) {
      callback = timeout;
      timeout = null;
    }
    return Bluebird.resolve(hoistLock.aquireLock(name, timeout))
      .nodeify(callback);
  },
  timeout: new TimeoutAPI(),
  Context: Context,
  _model: Model,
  bucket: new BucketAPI()
};

index.event = index.events;

export default index;

'use strict';
import config from 'config';
import redisLock from 'redis-lock';
import redis from 'redis';
import BaseAPI from './base_api';

let client;

class HoistLockAPI extends BaseAPI {
  constructor() {
    super();
    this.client = HoistLockAPI._getClient();
    this.lock = redisLock(this.client);
  }
  aquireLock(name, timeout) {

    timeout = timeout || 500;
    return new Promise((resolve) => {
      this.lock(name, timeout, function (done) {
        this.release = done;
        resolve(this);
      });
    });
  }
}


HoistLockAPI._getClient = function () {
  return client || (client = redis.createClient(config.get('Hoist.redis.port'), config.get('Hoist.redis.host')));
};



export default HoistLockAPI;

'use strict';
import Bluebird from 'bluebird';
import {
  isFunction
}
from 'lodash';

export function Log() {

  let args = Array.prototype.slice.call(arguments);
  let callback;
  if (isFunction(args[args.length - 1])) {
    callback = args.pop();
  }
  let log = {
    type: 'appLog',
    arguments: args
  };

  let cache = [];
  let message = JSON.stringify(log, function (key, value) {
    if (typeof value === 'object' && value !== null) {
      if (cache.indexOf(value) !== -1) {
        return '[Circular]';
      }
      // Store value in our collection
      cache.push(value);
    }
    return value;
  });
  cache = null;
  console.log(message);

  //return promise for backwards compatibility

  return Bluebird.resolve(null).nodeify(callback);
}

'use strict';
import Context from '@hoist/context';
import Errors from '@hoist/errors';
import {
  isNumber
}
from 'lodash';
import BaseAPI from './base_api';

class TimeoutAPI extends BaseAPI {
  reset(milliseconds) {
    //upper bound at 2m
    //lower bound at 1s
    return Promise.resolve().then(() => {

      if (!milliseconds || !isNumber(milliseconds) || milliseconds > 120000 || milliseconds < 1) {
        throw new Errors.timeout.InvalidTimeoutValueError();
      }

      return Context.get().then((context) => {
        if (context.timeout) {
          clearTimeout(context.timeout.current);
          context.timeout.current = setTimeout(context.timeout.onTimeout, milliseconds);
        }
      });
    });

  }
}


export default TimeoutAPI;

'use strict';
import Errors from '@hoist/errors';
import Pipeline from '@hoist/user-pipeline';
import BaseAPI from './base_api';
import Bluebird from 'bluebird';

class UserAPI extends BaseAPI {
  constructor() {
    super();
    this.pipeline = new Pipeline();
  }
  login(username, password, callback) {
    if (!username) {
      throw new Errors.user.request.InvalidError('username is required');
    }
    if (!password) {
      throw new Errors.user.request.InvalidError('password is required');
    }
    return Bluebird.resolve(this.pipeline.login(username, password))
      .nodeify(callback);
  }
  invite(userDetails, callback) {
    if (!userDetails) {
      throw new Errors.user.request.InvalidError('user details are required');
    }
    return Bluebird.resolve(this.pipeline.invite(userDetails))
      .nodeify(callback);
  }
}

export default UserAPI;

'use strict';
var Hoist = require('../../src');
var expect = require('chai').expect;
var sinon = require('sinon');
var BBPromise = require('bluebird');
var bucketPipeline = require('@hoist/bucket-pipeline').Pipeline;
var HoistErrors = require('@hoist/errors');

describe('Hoist.bucket', function () {
  it('exists', function () {
    return expect(Hoist.bucket).to.exist;
  });
  describe('.remove', function () {
    before(function () {
      sinon.stub(bucketPipeline.prototype, 'remove').returns(BBPromise.resolve(null));
      return Hoist.bucket.remove('key');
    });
    after(function () {
      bucketPipeline.prototype.remove.restore();
    });
    it('calls bucketPipeline.remove', function () {
      return expect(bucketPipeline.prototype.remove)
        .to.have.been.calledWith('key');
    });
  });
  describe('.set', function () {
    describe('with key and create', function () {
      var fakeKey = 'key';
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'set').returns(BBPromise.resolve());
        return Hoist.bucket.set(fakeKey, true);
      });
      after(function () {
        bucketPipeline.prototype.set.restore();
      });
      it('calls bucketPipeline.set with correct key', function () {
        expect(bucketPipeline.prototype.set)
          .to.have.been.calledWith(fakeKey, true);
      });
      it('with callback', function (done) {
        Hoist.bucket.set(fakeKey, true, done);
      });
    });
    describe('with key and callback', function () {
      var fakeKey = 'key';
      before(function (done) {
        sinon.stub(bucketPipeline.prototype, 'set').returns(BBPromise.resolve());
        return Hoist.bucket.set(fakeKey, done);
      });
      after(function () {
        bucketPipeline.prototype.set.restore();
      });
      it('calls bucketPipeline.set with correct key and meta', function () {
        expect(bucketPipeline.prototype.set)
          .to.have.been.calledWith(fakeKey, null);
      });
    });
    describe('with no key specified', function () {
      it('rejects', function () {
        expect(function () {
          Hoist.bucket.set();
        }).to.throw(HoistErrors.bucket.InvalidError);
      });
      it('with callback', function () {
        expect(function () {
          Hoist.bucket.set(function () {});
        }).to.throw(HoistErrors.bucket.InvalidError);
      });
    });
  });
  describe('.add', function () {
    describe('with key and meta', function () {
      var fakeKey = 'key';
      var fakeMeta = 'meta';
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'add').returns(BBPromise.resolve());
        return Hoist.bucket.add(fakeKey, fakeMeta);
      });
      after(function () {
        bucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(bucketPipeline.prototype.add)
          .to.have.been.calledWith(fakeKey, fakeMeta);
      });
      it('with callback', function (done) {
        Hoist.bucket.add(fakeKey, fakeMeta, done);
      });
    });
    describe('with key and callback', function () {
      var fakeKey = 'key';
      before(function (done) {
        sinon.stub(bucketPipeline.prototype, 'add').returns(BBPromise.resolve());
        return Hoist.bucket.add(fakeKey, done);
      });
      after(function () {
        bucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(bucketPipeline.prototype.add)
          .to.have.been.calledWith(fakeKey, null);
      });
    });
    describe('with only callback', function () {
      before(function (done) {
        sinon.stub(bucketPipeline.prototype, 'add').returns(BBPromise.resolve());
        return Hoist.bucket.add(done);
      });
      after(function () {
        bucketPipeline.prototype.add.restore();
      });
      it('calls bucketPipeline.add with correct key and meta', function () {
        expect(bucketPipeline.prototype.add)
          .to.have.been.calledWith(null, null);
      });
    });
  });
  describe('.get', function () {
    describe('with key', function () {
      var fakeKey = 'key';
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'get').returns(BBPromise.resolve());
        return Hoist.bucket.get(fakeKey);
      });
      after(function () {
        bucketPipeline.prototype.get.restore();
      });
      it('calls bucketPipeline.get with correct key', function () {
        expect(bucketPipeline.prototype.get)
          .to.have.been.calledWith(fakeKey);
      });
      it('with callback', function (done) {
        Hoist.bucket.get(fakeKey, done);
      });
    });
    describe('with only callback', function () {
      before(function (done) {
        sinon.stub(bucketPipeline.prototype, 'get').returns(BBPromise.resolve());
        Hoist.bucket.get(done);
      });
      after(function () {
        bucketPipeline.prototype.get.restore();
      });
      it('calls bucketPipeline.get with no key', function () {
        expect(bucketPipeline.prototype.get)
          .to.have.been.calledWith();
      });
    });
  });
  describe('.getAll', function () {
    before(function () {
      sinon.stub(bucketPipeline.prototype, 'getAll').returns(BBPromise.resolve([]));
      return Hoist.bucket.getAll();
    });
    after(function () {
      bucketPipeline.prototype.getAll.restore();
    });
    it('calls bucketPipeline.get with correct key', function () {
      expect(bucketPipeline.prototype.getAll)
        .to.have.been.calledWith();
    });
    it('with callback', function (done) {
      Hoist.bucket.getAll(done);
    });
  });
  describe('.each', function () {
    describe('with a function', function () {
      var fn = function () {};
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'each').returns(BBPromise.resolve());
        return Hoist.bucket.each(fn);
      });
      after(function () {
        bucketPipeline.prototype.each.restore();
      });
      it('calls bucketPipeline.each with correct key', function () {
        expect(bucketPipeline.prototype.each)
          .to.have.been.calledWith(fn);
      });
      it('with callback', function (done) {
        Hoist.bucket.each(fn, done);
      });
    });
    describe('without function', function () {
      it('rejects', function () {
        expect(function () {
          Hoist.bucket.each();
        }).to.throw(HoistErrors.bucket.InvalidError);
      });
    });
  });
  describe('.saveMeta', function () {
    describe('with meta and key', function () {
      var fakeMeta = {
        key: 'value'
      };
      var fakeKey = 'key';
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'saveMeta').returns(BBPromise.resolve());
        return Hoist.bucket.saveMeta(fakeMeta, fakeKey);
      });
      after(function () {
        bucketPipeline.prototype.saveMeta.restore();
      });
      it('calls bucketPipeline.saveMeta with correct key', function () {
        expect(bucketPipeline.prototype.saveMeta)
          .to.have.been.calledWith(fakeMeta, fakeKey);
      });
      it('with callback', function (done) {
        Hoist.bucket.saveMeta(fakeMeta, fakeKey, done);
      });
    });
    describe('with meta and no key', function () {
      var fakeMeta = {
        key: 'value'
      };
      before(function () {
        sinon.stub(bucketPipeline.prototype, 'saveMeta').returns(BBPromise.resolve());
        return Hoist.bucket.saveMeta(fakeMeta);
      });
      after(function () {
        bucketPipeline.prototype.saveMeta.restore();
      });
      it('calls bucketPipeline.saveMeta with correct key', function () {
        expect(bucketPipeline.prototype.saveMeta)
          .to.have.been.calledWith(fakeMeta, undefined);
      });
    });
    describe('with meta and no key and callback', function () {
      var fakeMeta = {
        key: 'value'
      };
      before(function (done) {
        sinon.stub(bucketPipeline.prototype, 'saveMeta').returns(BBPromise.resolve());
        return Hoist.bucket.saveMeta(fakeMeta, done);
      });
      after(function () {
        bucketPipeline.prototype.saveMeta.restore();
      });
      it('calls bucketPipeline.saveMeta with correct key', function () {
        expect(bucketPipeline.prototype.saveMeta)
          .to.have.been.calledWith(fakeMeta, null);
      });
    });
  });
});

'use strict';
var ConnectorPipeline = require('@hoist/connector-pipeline').Pipeline;
var Hoist = require('../../lib');
var sinon = require('sinon');
var expect = require('chai').expect;
var errors = require('@hoist/errors');
var BBPromise = require('bluebird');

describe('Hoist', function () {
  describe('.connector', function () {
    var StubConnector = function () {
      this.get = sinon.stub();
      this.put = sinon.stub();
      this.post = sinon.stub();
      this.delete = sinon.stub();
    };
    var connector;
    var stubConnector = new StubConnector();
    describe('with key', function () {
      before(function () {
        sinon.stub(ConnectorPipeline.prototype, 'loadConnector').returns(BBPromise.resolve(stubConnector));
        connector = Hoist.connector('key');
      });
      after(function () {
        ConnectorPipeline.prototype.loadConnector.restore();
      });
      it('loads the specified connector', function () {
        expect(ConnectorPipeline.prototype.loadConnector)
          .to.have.been.calledWith('key');
      });
      describe('#get', function () {

        var _promise = BBPromise.resolve(true);
        before(function () {
          stubConnector.get.returns(_promise);
          return connector.get('/path?query');
        });
        after(function () {
          stubConnector.get.reset();
        });
        it('calls pipeline#get', function () {
          expect(stubConnector.get)
            .to.have.been.calledWith('/path?query');
        });
      });
      describe('#put', function () {

        var _promise = BBPromise.resolve(true);
        before(function () {
          stubConnector.put.returns(_promise);
          return connector.put('/path?query', 'data');
        });
        after(function () {
          stubConnector.put.reset();
        });
        it('calls pipeline#put', function () {
          expect(stubConnector.put)
            .to.have.been.calledWith('/path?query', 'data');
        });
      });
      describe('#delete', function () {

        var _promise = BBPromise.resolve(true);
        before(function () {
          stubConnector.delete.returns(_promise);
          return connector.delete('/path');
        });
        after(function () {
          stubConnector.delete.reset();
        });
        it('calls pipeline#delete', function () {
          expect(stubConnector.delete)
            .to.have.been.calledWith('/path');
        });
      });
      describe('#post', function () {

        var _promise = BBPromise.resolve(true);
        before(function () {
          stubConnector.post.returns(_promise);
          return connector.post('/path?query', 'data');
        });
        after(function () {
          stubConnector.post.reset();
